// PI-Regler mit Anti-Windup (Clamping) und Begrenzung von -800 bis +800

const Kp = 0.4;     // Proportionalfaktor
const Ki = 0.1;     // Integralfaktor

// Einlesen der ZustÃ¤nde
let fehler = msg.payload;
let now = Date.now();
let lastTime = context.get("lastTime") || now;
let dt = Math.max((now - lastTime) / 1000, 0.001); // Zeitdifferenz, min. 1 ms

context.set("lastTime", now);

// Reglergrenzen
const outputMin = -800;
const outputMax = 800;

// Integratorstatus
let integral = context.get("integral") || 0;
let newIntegral = integral + fehler * dt;

// PI-Ausgang berechnen (vor Clamping)
let output = Kp * fehler + Ki * newIntegral;

// Anti-Windup durch Clamping
if (output > outputMax) {
    output = outputMax;
    if (fehler < 0) integral = newIntegral;
} else if (output < outputMin) {
    output = outputMin;
    if (fehler > 0) integral = newIntegral;
} else {
    integral = newIntegral;
}

// Zustand speichern
context.set("integral", integral);

// Ausgang setzen
msg.payload = output;
return msg;
