const Kp = 0.4; // Proportionalverstärkung
const Ki = 0.1; // Integralverstärkung

// Sollwert ist 0
const sollwert = 0;
const istwert = msg.payload;
const fehler = istwert - sollwert;

// Integral-Teil (über Flow-Kontext speichern)
let integral = flow.get('integral') || 0;
integral += fehler;

// Begrenzung des Integrals, um Aufschaukeln zu verhindern
const integralMax = 5000;
integral = Math.min(Math.max(integral, -integralMax), integralMax);
flow.set('integral', integral);

// Regelung durch PI-Algorithmus
const output = (Kp * fehler) + (Ki * integral);

msg.payload = output;
return msg;

