let summe = p1 + p2 + p3;

msg.payload = summe;
return msg;

function mapBatteryStatusToGerman(status) {
    switch (status) {
        case "discharge":
            return "Speicher L1 - Entladen";
        case "standby":
            return "Speicher L1 - Ruhezustand";
        case "charge":
            return "Speicher L1 - Laden";
        default:
            return status;
    }
}

function mapGermanToColor(text) {
    switch (text) {
        case "Speicher L1 - Entladen":
            return "red";
        case "Speicher L1 - Laden":
            return "green";
        case "Speicher L1 - Ruhezustand":
            return "orange";
        default:
            return "";
    }
}

function mapGermanToIcon(text) {
    switch (text) {
        case "Speicher L1 - Laden":
            return "autorenew";
        case "Speicher L1 - Entladen":
            return "cached";
        case "Speicher L1 - Ruhezustand":
            return "pause_circle_outline";
        default:
            return "";
    }
}

msg.payload = msg.payload.bat_sts;

const germanStatus = mapBatteryStatusToGerman(msg.payload);
msg.payload2 = germanStatus;

msg.payload = mapGermanToColor(germanStatus);
msg.payload3 = mapGermanToIcon(germanStatus);

return msg;

