let p1 = global.get("P1") || 0;
let p2 = global.get("P2") || 0;
let p3 = global.get("P3") || 0;
let p4 = global.get("P4") || 0;

let summe = p1 + p2 + p3 + p4;

msg.payload = summe;
return msg;

function mapBatteryStatusToGerman(status) {
    switch (status) {
        case "discharge":
            return "Speicher 1 - Entladen";
        case "standby":
            return "Speicher 1 - Ruhezustand";
        case "charge":
            return "Speicher 1 - Laden";
        default:
            return status;
    }
}

function mapGermanToColor(text) {
    switch (text) {
        case "Speicher 1 - Entladen":
            return "red";
        case "Speicher 1 - Laden":
            return "green";
        case "Speicher 1 - Ruhezustand":
            return "orange";
        default:
            return "";
    }
}

function mapGermanToIcon(text) {
    switch (text) {
        case "Speicher 1 - Laden":
            return "autorenew";
        case "Speicher 1 - Entladen":
            return "cached";
        case "Speicher 1 - Ruhezustand":
            return "pause_circle_outline";
        default:
            return "";
    }
}

msg.payload = msg.payload.bat_sts;

const germanStatus = mapBatteryStatusToGerman(msg.payload);
msg.payload2 = germanStatus;

msg.payload = mapGermanToColor(germanStatus);
msg.payload3 = mapGermanToIcon(germanStatus);

return msg;

